mapper.select:
select *
from xiafish.user

    user_id = #{userId},
    and user_name like concat('%',#{userName},'%') 
    and user_gender = #{userGender} 
    and user_phone_num like concat('%',#{userPhoneNum},'%') 
    and user_email like concat('%',#{userEmail},'%') 
    and user_passwd = #{userPasswd} 
    and user_status = #{userStatus} 
    and user_photo = #{userPhoto} 
    and user_campus like concat('%',#{userCampus},'%') 
    and user_nickname like concat('%',#{userNickname},'%') 
    and user_profile like concat('%',#{userProfile},'%')

mapper.update:
UPDATE user
user_id = #{userId}, user_name = #{userName}, user_gender = #{userGender}, user_phone_num = #{userPhoneNum}, user_email = #{userEmail}, user_passwd=#{userPasswd}, user_status=#{userStatus}, user_photo = #{userPhoto}, user_campus = #{userCampus}, user_nickname = #{userNickname}, user_profile = #{userProfile},
WHERE user_id = #{userId}
